import { createStore } from 'vuex';

export default createStore({
  state: {
    cep: '',
    endereco: null,
    error: null,
  },
  mutations: {
    setCep(state, cep) {
      state.cep = cep;
    },
    setEndereco(state, endereco) {
      state.endereco = endereco;
    },
    setError(state, error) {
      state.error = error;
    },
  },
  actions: {
    async pesquisarCEP({ commit }, cep) {
      commit('setError', null);
      commit('setEndereco', null);

      const cepLimpo = cep.replace("-", "");

      try {
        const response = await fetch(`http://localhost:8000/api/cep?cep=${cepLimpo}`);
        if (!response.ok) {
          throw new Error("Erro ao buscar o CEP. Verifique o número e tente novamente.");
        }
        const data = await response.json();
        commit('setEndereco', data);
      } catch (err) {
        commit('setError', err.message);
      }
    },
  },
  getters: {
    getCep: (state) => state.cep,
    getEndereco: (state) => state.endereco,
    getError: (state) => state.error,
  },
});
